document.querySelectorAll(".luminomorph-button").forEach(l=>{l.addEventListener("click",function(e){const t=document.createElement("span");t.classList.add("ripple");const n=this.getBoundingClientRect();t.style.left=`${e.clientX-n.left}px`,t.style.top=`${e.clientY-n.top}px`,this.appendChild(t),setTimeout(()=>t.remove(),600)})});
